import React, {useState, useEffect} from "react";
import { View, Text, Button, StyleSheet, TouchableOpacity, ActivityIndicator, FlatList } from "react-native";



export function SettingsScreen({ route, navigation }) {

  const[isLoading, setLoading] = useState(true);
  const[data, setData] = useState([]);

  const getData = async () => {
    try {
      const response = await fetch("https://api.sampleapis.com/rickandmorty/characters")
      const json = await response.json()
      setData(json)
    } catch (error) {
      console.log(error)
    }finally{
      setLoading(false)
    }
  } 

  useEffect(()=>{
    getData();
  }, [])

  function handleHomePress() {
    navigation.navigate("Home");
  }
  return (
    <View style={styles.screen}>
      <Text>The Settings Screen!</Text>
      <Button title="Go to the Home screen!" onPress={handleHomePress} />

      <View>
        {isLoading ? (
          <ActivityIndicator/>
        ) : (
          <FlatList
          data={data}
          keyExtractor={({id}, index) => id}
          renderItem={({item}) => (
            <View style={styles.itemWrapper}>

            </View>
          )}
          />
        )}
      </View>

    </View>
  );
}

const styles = StyleSheet.create({
  screen: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
});
